tutorialsIndex_en = {"Basic", "Intermediate", "Advanced", "Profissions", "Recipes - Doctor", "Recipes - Cooker", "Manual - Constructor"}

tutorialsInfo_en = {
    {
        {name = "01. Introduction", text = "Icone 1 Representa o Controle de Combate.\n\nIcone 2 Representa o Battle Window.\n\nIcone 3 Representa seu Inventario.\n\nIcone 4 Representa sua VIP LIST.\n\nIcone 6 Representa suas Skills.\n\nIcone 7 Representa o Seu MINIMAP.\n\nIcone 8 Representa a barra de navegação.\n\nIcone 9 Representa a Sua Barra de Magias.", img = "imgs/b1"}, 
        {name = "02. Handling Itens", text = "Para manusear itens, apenas clique e segure com o botão esquerdo do mouse e arraste-o para onde quiser, você pode mover itens de dentro ou fora de seu inventário, e também coletá-los ou joga-los fora.\n\nEntretanto existem itens únicos que não podem ser descartados.", img = "imgs/b2"}, 
        {name = "03. Attacking Enemies", text = "Para atacar um inimigo, apenas clique em seu alvo (botão direito do mouse), depois que o alvo for marcado, você vai atacar o inimigo automaticamente.", img = "imgs/b4"}, 
        {name = "04. Using attacks", text = "Seus ataques são desbloqueados conforme você sobe de level. Para usar um ataque apenas clique em seu ícone (botão esquerdo do mouse) ou utilize os atalhos 'F1, F2, F3, F4, ...'. Os atalhos também podem ser redefinidos nas configurações de Hotkeys.", img = "imgs/b5"}, 
        {name = "05. Creature Loot", text = "Toda criatura depois de derrotado pode conter itens, para coletá-los, apenas clique na criatura caida (botão direito do mouse).", img = "imgs/b6"}, 
        {name = "06. Restoring health/energy", text = "Para recuperar sua vida, basta comer algum tipo de comida, que pode ser dropada nas criaturas. Outra maneira é usando poção de vida (como mostra a figura).", img = "imgs/b8"}, 
        {name = "07. Map", text = "O mapa do jogo pode ser aberto com o atalho 'Ctrl+M' ou por seu ícone.\n\nO mapa mostra localizações importantes como cidades e lojas.\n\nPara visualizar o mapa por inteiro, use 'Ctrl+Shift+M'.", img = "imgs/b9"}, 
		{name = "08. Market", text = "Para abrir uma loja, incie uma conversação com o e npc diga:\n\n-Hi.\n\n-Trade.", img = "imgs/b10"}, 
        {name = "09. Itens Deposit", text = "O depósito pode ser encontrado em qualquer Centro de depósitos, para abrir, apenas clique no depósito (botão direito do mouse). No depósito você pode guardar quantos itens quiser.", img = "imgs/b12"}
    },
    {
        {name = "10. Olhar itens", text = "Para olhar algo, posicione o mouse e pressione os 2 botões ao mesmo tempo.\n\nOutra maneira de olhar algo é utilizando 'Ctrl'+botão direito do mouse e selecionando a opção 'Olhar'.\n\nOlhar é muito útil para pegar informações de itens, principalmente para saber o nome deles.", img = "imgs/i1"},
        {name = "11. Controles de combate", text = "Controles de combate são muito importantes, principalmente para a caça.\n\nOffensive: Ataque extra, porém um pouco da defesa é perdida.\n\nBalanced: Força balanceada\n\nDefensive: Defesa extra porém um pouco do ataque perdido.\n\nFollow attack: Essa opção permite que você siga automaticamente a criatura que está atacando.\n\nEnable PvP: Habilitando essa opção você vai poder atacar outras pessoas, porém o PvP só é permitido depois de um certo Level.", img = "imgs/i5"},
        {name = "12. Hotkeys", text = "Aqui você pode adicionar novos atalhos e até colocar itens como potions, ou comandos de batalha como ataques etc.", img = "imgs/i6"},
        {name = "13. Aceitando Quests", text = "Para aceitar quests básicas é muito simples, comece a conversa com o NPC com a palavra 'hi', depois ofereça ajuda para o NPC com 'help', então aceite a quest com 'yes'.\n\nQuando terminar a tarefa pedida, volte no NPC e repita o mesmo processo.\n\nQuests mais complexas exigem sua própria interpretação para descobrir o diálogo envolvido.", img = "imgs/i7"},
        {name = "24. Lista de amigos", text = "A lista de amigos mostra se seus amigos estão Online ou Offline no jogo, além de fornecer várias opções como mensagens privadas ou ignorar.\n\nPara adicionar um amigo é muito fácil, apenas clique com o botão direito na lista, selecione a opção de adicionar e digite o nome do jogador que deseja adicionar.\n\nOutro meio de adicionar alguém, é segurando 'Ctrl' e clicando no jogador que deseja adicionar, depois selecionar a opção.", img = "imgs/i12"},
    },	
    {
        {name = "15. Trade", text = "Para fazer uma troca, segure 'Ctrl' e clique com o botão direito do mouse no item que deseja trocar e selecione 'Trocar com', depois use no jogador que você deseja fazer a troca. (no caso de vários itens, coloque-os em uma bag e troque a bag).", img = "imgs/a1"},
        {name = "16. Cortar árvore", text = "Para cortar uma árvore, clique com o botão direito do mouse em sua axe e depois com o botão esquerdo, clique nas árvores próprias para o corte (semelhante ou igual a imagem acima).", img = "imgs/a5"},
        {name = "17. Pesca", text = "Para pescar, basta clicar com o botão direito do mouse na vara de pescar (que deve estar em sua mão) e use em algum lago ou mar (botão esquerdo), lembrando que você deve ter minhocas em sua bag ou cintura para poder pescar. Quando algum peixe morder a isca (água se movimenta em volta da isca), clique em cima da isca com o botão direito novamente.\n\nNovos peixes aparecem dependendo do seu level de pesca.\n\nDica: Iscas podem ser compradas no NPC Fiherman.", img = "imgs/i3"},
        {name = "18. Casas", text = "Para comprar uma casa, você precisa ter VIP account e pelo menos level 30. \n\nPrimeiramente encontre uma casa sem dono, 'olhe' a porta da casa para verificar e saber o preço, depois fique de frente para a porta da casa e digite !buyhouse.\n\nTodo mês será debitado o preço da casa do seu depósito, caso contrário, você perderá sua casa (os itens são enviados para o depósito automaticamente). Para ver informações sobre o aluguel digite !houseinfo\n\nO dono da casa possui vários comandos:\n!invite - Para convidar alguém para a sua casa.\n!viceowner - Para definir um segundo dono para a casa\n!door - Para definir quem pode abrir determinada porta (fique de frente para a porta em questão).\n!housekick player - Para jogar a pessoa para fora de sua casa.\n!sellhouse player - Para vender a casa para algum jogador.\n!leavehouse Para abandonar a casa.", img = "imgs/a2"},
        {name = "19. Party e Channels", text = "1. Private Chat Channel (para criar um canal particular e convidar jogadores)\n3. Trade Center (Sala de comércio, para compra e venda de itens)\n4. Help (Sala de ajuda, para perguntas a tutores)", img = "imgs/a4"},
    },	
    {
        {name = "20. Escolhendo profissão", text = "Para escolher sua profissão, vá até o Npc respectivo da profissão escolhida e diga:\n\n-Hi.\n\n-help\n\nConstructor. (ou a profissão que você preferir)\n\n-Yes.", img = "imgs/h1"},
        {name = "21. Construtor",           text = "Para treinar a profissão de Marceneiro, vá até o Npc Constructor Tutor e diga:\n\n-Hi.\n\n-Enter.\n\n-Yes.\n\n- Abra a mesa de marceneiro clicando com o botão direito do mouse em cima dela ou pressionando Crtl+Botão direito e depois escolhendo a opção \"open\", coloque as woods dentro da mesa e depois clique novamente com o botão direito em cima dela para fazer o trabalho.\n\nObs: As instruções de marcenaria estão no site.", img = "imgs/h2"},
        {name = "22. Médico",               text = "Para treinar a profissão de Doutor, vá até o Npc Doctor e diga:\n\n-Hi.\n\n-Enter.\n\n-Yes.\n\n- Abra o frasco de poções da mesa clicando com o botão direito do mouse em cima dele ou pressionando Crtl+Botão direito e depois escolhendo a opção \"open\", coloque os ingredientes da poção dentro do frasco de poções e depois clique novamente com o botão direito em cima dele para preparar a poção.\n\nObs: As fórmulas estão no site.", img = "imgs/h3"},
        {name = "23. Cozinheiro",           text = "Para treinar a profissão de Cozinheiro, vá até o Npc Cooking Tutor e diga:\n\n-Hi.\n\n-Enter.\n\n-Yes.\n\n- Abra o foção clicando com o botão direito do mouse em cima dele ou pressionando Crtl+Botão direito e depois escolhendo a opção \"open\", coloque os ingredientes da receita dentro do fogão e depois clique novamente com o botão direito em cima dele para preparar a receita.\n\nObs: As receitas estão no site.", img = "imgs/h4"},
    },	
