tutorialsIndex_pt = {"Básico", "Intermediário", "Avançado", "Profissões", "Agricultura - Seeds", "Receitas - Doutor", "Receitas - Cozinheiro", "Manual - Construtor"}

tutorialsInfo_pt = {
    {
        {name = "01. Introdução", text = "Icone 1: Representa a Janela de Batalha (onde encontrarão criaturas e players por perto).\n\Icone 2: Representa o Controle de Combate.\n\Icone 3: Representa as informações de vida e energia.\n\Icone 4: Representa seu inventário.\n\Icone 5: Representa o mapa.\n\Icone 6: Representa sua janela de navegação.\n\Icone 7: Representa seus Skills.\n\Icone 8: Representa sua lista de amigos.\n\Icone 9: Representa a lista de suas realizações.\n\Icone 10: Representa as suas magias e ataques.", img = "imgs/b1"}, 
        {name = "02. Manuseando Itens", text = "Para manusear itens, apenas clique e segure com o botão esquerdo do mouse e arraste-o para onde quiser, você pode mover itens de dentro ou fora de seu inventário, e também coletá-los ou joga-los fora.\n\nEntretanto existem itens únicos que não podem ser descartados.", img = "imgs/b2"}, 
        {name = "03. Atacando inimigos", text = "Para atacar um inimigo, apenas clique em seu alvo (botão direito do mouse), depois que o alvo for marcado, você vai atacar o inimigo automaticamente.", img = "imgs/b3"}, 
        {name = "04. Usando ataques", text = "Seus ataques são desbloqueados conforme você sobe de level. Para usar um ataque apenas clique em seu ícone (botão esquerdo do mouse) ou utilize os atalhos 'F1, F2, F3, F4, ...'. Os atalhos também podem ser redefinidos nas configurações de Hotkeys.", img = "imgs/b4"}, 
        {name = "05. Loot das criaturas", text = "Toda criatura depois de derrotado pode conter itens, para coletá-los, apenas clique na criatura caida (botão direito do mouse).", img = "imgs/b5"}, 
        {name = "06. Restaurando vida/energia", text = "Para recuperar sua vida, basta comer algum tipo de comida, que pode ser dropada nas criaturas. Outra maneira é usando poção de vida (como mostra a figura).\n\Para usar poções, clique com o botão direito em cima dela e depois clique com o esquerdo em cima de você ou jogador que queira usar a potion. Outra maneira é usando Hotkeys.", img = "imgs/b6"}, 
        {name = "07. Mapa", text = "O mapa do jogo pode ser aberto com o atalho 'Ctrl+M' ou por seu ícone.\n\nO mapa mostra localizações importantes como cidades e lojas.\n\nPara visualizar o mapa por inteiro, use 'Ctrl+Shift+M'.", img = "imgs/b7"}, 
		{name = "08. Mercado", text = "Para abrir o inventário de uma loja, incie uma conversação com o e npc diga:\n\n-Hi.\n\n-Trade.", img = "imgs/b8"}, 
        {name = "09. Depósito", text = "O depósito pode ser encontrado em qualquer Centro de depósitos, para abrir, apenas clique no depósito (botão direito do mouse). No depósito você pode guardar quantos itens quiser.", img = "imgs/b9"}
    },
    {
        {name = "01. Olhar itens", text = "Para olhar algo, posicione o mouse e pressione os 2 botões ao mesmo tempo.\n\nOutra maneira de olhar algo é utilizando 'Ctrl'+botão direito do mouse e selecionando a opção 'Olhar'.\n\nOlhar é muito útil para pegar informações de itens, principalmente para saber o nome deles.", img = "imgs/i1"},
        {name = "02. Controles de combate", text = "Controles de combate são muito importantes, principalmente para a caça.\n\nOffensive: Ataque extra, porém um pouco da defesa é perdida.\n\nBalanced: Força balanceada\n\nDefensive: Defesa extra porém um pouco do ataque perdido.\n\nFollow attack: Essa opção permite que você siga automaticamente a criatura que está atacando.\n\nEnable PvP: Habilitando essa opção você vai poder atacar outras pessoas, porém o PvP só é permitido depois de um certo Level.", img = "imgs/i2"},
        {name = "03. Hotkeys", text = "Aqui você pode adicionar novos atalhos e até colocar itens como potions, ou comandos de batalha como ataques etc.", img = "imgs/i3"},
        {name = "04. Aceitando Quests", text = "Para aceitar quests básicas é muito simples, comece a conversa com o NPC com a palavra 'hi', depois ofereça ajuda para o NPC com 'help', então aceite a quest com 'yes'.\n\nQuando terminar a tarefa pedida, volte no NPC e repita o mesmo processo.\n\nQuests mais complexas exigem sua própria interpretação para descobrir o diálogo envolvido.", img = "imgs/i4"},
        {name = "05. Lista de amigos", text = "A lista de amigos mostra se seus amigos estão Online ou Offline no jogo, além de fornecer várias opções como mensagens privadas ou ignorar.\n\nPara adicionar um amigo é muito fácil, apenas clique com o botão direito na lista, selecione a opção de adicionar e digite o nome do jogador que deseja adicionar.\n\nOutro meio de adicionar alguém, é segurando 'Ctrl' e clicando no jogador que deseja adicionar, depois selecionar a opção.", img = "imgs/i5"},
    },	
    {
        {name = "01. Trade", text = "Para fazer uma troca, segure 'Ctrl' e clique com o botão direito do mouse no item que deseja trocar e selecione 'Trocar com', depois use no jogador que você deseja fazer a troca. (no caso de vários itens, coloque-os em uma bag e troque a bag).", img = "imgs/a1"},
        {name = "02. Cortar árvore", text = "Para cortar uma árvore, clique com o botão direito do mouse em sua axe e depois com o botão esquerdo, clique nas árvores próprias para o corte (semelhante ou igual a imagem acima).", img = "imgs/a2"},
        {name = "03. Mineração", text = "Para minerar uma rocha, clique com o botão direito do mouse em sua pickaxe e depois com o botão esquerdo, clique nas rochas próprias para o mineração (semelhante ou igual a imagem acima).", img = "imgs/a3"},
        {name = "04. Agricultura", text = "Para plantar algo, fale com o NPC Donald e compre a semente que desejar (lembrando que quanto maior for seu level de plantação, mais opções de plantio você terá). Com a enxada, are a terra clicando com o botão direito do mouse em cima da enxada, e em seguida, com o botão esquerdo por cima do solo próprio para plantio, logo depois, clique com o botão direito em cima da semente e novamente com o esquerdo, clique em cima do solo que você arou.\n\Para ver quanto tempo resta para colher seu plantio, diga para o Donald:\n\Hi.\n\List.\n\Para coletar seu plantio, diga para o Donald:\n\Hi.\n\Collect.", img = "imgs/a4"},
        {name = "05. Fazer Kaen Stone", text = "Para fazer a Kaen Stone, arraste a Kaen Boshi em cima da Small Stone ou vice-versa.\n\\n\Itens necessários: Kaen Boshi, Small Stone.\n\Level necessário: 15.\n\Observação 1: Você pode fazer quantas Kaen Stones quiser de uma vez, desde que o número de Kaen Boshis e Small Stones sejam iguais.\n\Observação 2: É necessário ser o Usopp para fazer.", img = "imgs/a5"},
        {name = "06. Fazer Kemuri Stone", text = "Para fazer a Kemuri Stone, arraste a Kemuri Boshi em cima da Small Stone ou vice-versa.\n\\n\Itens necessários: Kemuri Boshi, Small Stone.\n\Level necessário: 20.\n\Observação 1: Você pode fazer quantas Kemuri Stones quiser de uma vez, desde que o número de Kemuri Boshis e Small Stones sejam iguais.\n\Observação 2: É necessário ser o Usopp para fazer.", img = "imgs/a6"},
        {name = "07. Fazer Pop Stone", text = "Para fazer a Pop Stone, arraste a Pop Green em cima da Small Stone ou vice-versa.\n\\n\Itens necessários: Pop Green, Small Stone.\n\Level necessário: 30.\n\Observação 1: Você pode fazer quantas Pop Stones quiser de uma vez, desde que o número de Pop Greens e Small Stones sejam iguais.\n\Observação 2: É necessário ser o Usopp para fazer.", img = "imgs/a7"},
        {name = "08. Pesca", text = "Para pescar, basta clicar com o botão direito do mouse na vara de pescar (que deve estar em sua mão) e use em algum lago ou mar (botão esquerdo), lembrando que você deve ter iscas (minhocas por exemplo) em sua bag ou cintura para poder pescar. Quando algum peixe morder a isca (água se movimenta em volta da isca), clique em cima da isca com o botão direito novamente.\n\nNovos peixes aparecem dependendo do seu level de pesca.\n\nDica: Iscas podem ser compradas no NPC Fiherman.", img = "imgs/a8"},
        {name = "09. Casas", text = "Para comprar uma casa, você precisa ter VIP account e pelo menos level 30.\n\Primeiramente encontre uma casa sem dono, 'olhe' a porta da casa para verificar e saber o preço, depois fique de frente para a porta da casa e digite !buyhouse.\n\Todo mês será debitado o preço da casa do seu depósito, caso contrário, você perderá sua casa (os itens são enviados para o depósito automaticamente). Para ver informações sobre o aluguel digite !houseinfo.\n\O dono da casa possui vários comandos:\n\!invite - Para convidar alguém para a sua casa.\n\!subowner - Para definir um segundo dono para a casa.\n\!door - Para definir quem pode abrir determinada porta (fique de frente para a porta em questão).\n\!kick player - Para jogar a pessoa para fora de sua casa.\n\!sellhouse player - Para vender a casa para algum jogador.\n\!leavehouse Para abandonar a casa.", img = "imgs/a9"},
        {name = "10. Party e Channels", text = "1. Private Chat Channel (para criar um canal particular e convidar jogadores)\n2. Game-Chat - Canal para conversas.\n3. Trade - Canal de comércio, para compra e venda de items.\n4. Help - Canal de ajuda, para perguntas a tutores.", img = "imgs/a10"},
    },	
    {
        {name = "01. Escolhendo profissão", text = "Para escolher sua profissão, vá até o Npc respectivo da profissão escolhida e diga:\n\n-Hi.\n\n-Help.\n\n-Yes.", img = "imgs/p1"},
        {name = "02. Construtor",           text = "Para treinar a profissão de Construtor, vá até o Npc Constructor Tutor e diga:\n\n-Hi.\n\n-Enter.\n\n-Yes.\n\n- Abra a mesa de construção clicando com o botão direito do mouse em cima dela ou pressionando Crtl+Botão direito e depois escolhendo a opção open, coloque o material que desejar para fazer o item especifico que você quer, você precisara ter um Toolkit em sua backpack, logo depois clique novamente com o botão direito em cima da mesa e clique em Work.\n\nObs: As instruções do Construtor estarão no guia, Manual  Constructor.", img = "imgs/p2"},
        {name = "03. Doutor",               text = "Para treinar a profissão de Doutor, vá até o Npc Doctor e diga:\n\n-Hi.\n\n-Enter.\n\n-Yes.\n\n- Abra o frasco de poções clicando com o botão direito do mouse em cima dela ou pressionando Crtl+Botão direito e depois escolhendo a opção open, coloque o ingrediente que desejar para fazer o item especifico que você quer, logo depois clique novamente com o botão direito em cima do frasco e clique em Work.\n\nObs: As instruções do Doutor estarão no guia, Receitas  Doutor.", img = "imgs/p3"},
        {name = "04. Cozinheiro",           text = "Para treinar a profissão de Cozinheiro, vá até o Npc Cooking Tutor e diga:\n\n-Hi.\n\n-Enter.\n\n-Yes.\n\n- Abra o fogão clicando com o botão direito do mouse em cima dele ou pressionando Crtl+Botão direito e depois escolhendo a opção open, coloque o ingrediente que desejar para fazer a receita especifica que você quer, logo depois clique novamente com o botão direito em cima do fogão e clique em Work.\n\nObs: As instruções do Cozinheiro estarão no guia, Receitas  Cozinheiro.", img = "imgs/p4"},
    },	
